import { Component, Input } from '@angular/core';
import { DashboardService } from '../../dashboard.service';

@Component({
  selector: 'sidebar-item',
  templateUrl: './sidebar-item.component.html',
})
export class SidebarItemComponent {
  @Input() title: string;
  @Input() routerLink: string;

  constructor(private dashboard: DashboardService) {
    this.title = '';
    this.routerLink = '';
  }

  currentRoute() {
    return this.dashboard.currentRoute
  }
}
<a routerLink="{{ routerLink }}">
  <span
    class="my-1 flex w-full items-center justify-start p-3 text-white {{
      currentRoute() === routerLink &&
        'border-white lg:border-red-300 border-l-4 lg:border-l-0 lg:border-r-4'
    }}"
  >
    <span>
      <ng-content />
    </span>
    <span class="mx-4 text-sm">
      {{ title }}
    </span>
  </span>
</a>
