import { Component, Input } from '@angular/core';
import { DashboardService } from '../../dashboard.service';

@Component({
  selector: 'sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css'],
})
export class SidebarComponent {
  @Input() mobileOrientation: 'start' | 'end';
  styles: {
    start: string;
    end: string;
  };
  constructor(private dashboard: DashboardService) {
    this.mobileOrientation = 'end';
    this.styles = {
      start: 'left-0',
      end: 'right-0',
    };
  }

  sidebarOpen() {
    return this.dashboard.sidebarOpen;
  }
}
<aside
  class="scrollbar bg-gray-900 overflow-y-auto h-screen top-0 lg:bg-transparent lg:block lg:relative lg:w-64 lg:z-auto
{{ styles[mobileOrientation] }}
{{ sidebarOpen() ? 'absolute z-40 w-8/12 sm:w-5/12' : 'hidden' }}"
>
  <div class="pb-32 lg:pb-6">
    <sidebar-header></sidebar-header>
    <sidebar-items></sidebar-items>
  </div>
</aside>
